import React from 'react'
import classes from "../../css/filterButton.module.css"
import { useState, useContext } from 'react'
import ShopContext from '../../shop-context'


function FilterButtton(props) {
  const [clicked ,setClicked] = useState(false)
  const shopContext = useContext( ShopContext)
  const click = ()=>{
    clicked ? setClicked(false) : setClicked(true)
    if(shopContext.size.length === 0){
      shopContext.setSize(props.type)
    }
    else if(shopContext.size === [props.type]){
      setClicked(false)
    }
    
  }

  return (
    <div className={`${classes.button} ${clicked? classes.clicked : ""}`} onClick={click}>
        <p>{props.type}</p>
    </div>
    )
  }



export default FilterButtton
