import React from 'react'
import classes from "../../css/filterPanel.module.css"
import Filter from '../Icons/Filter'
import FilterButtton from './FilterButtton'
import { useState } from 'react'

function FilterPanel(props) {
  const [showPanel, setShowPanel] = useState(false)
  const [animation, setAnimation] = useState(false)
  const [counter, setCounter] = useState(0);


  const controlPanel = ()=>{
    {showPanel? setShowPanel(false): setShowPanel(true)}
    
    setCounter(counter+1)
    if(counter%2===0){
      setAnimation(true)
    }else{
      setAnimation(false)
    }
    console.log(counter)
  }
    
  return (
    <div className={classes.panel}>
      {showPanel?<div className={`${showPanel && classes["panel-on"]  }`}>
      
      <FilterButtton type={"xs"}/>
      <FilterButtton type={"s"}/>
      <FilterButtton type={"m"}/>
      <FilterButtton type={"l"}/>
      <FilterButtton type={"xl"}/>
      </div> : "" }
      <div className={classes.filter}> <Filter onClick={controlPanel}/> </div>
    </div>
  )
  }


export default FilterPanel
