import React from 'react'
import Heart from "../Icons/Heart"
import HeartFull from "../Icons/HeartFull"
import classes from "../../css/card.module.css"
import { useState } from 'react'
import ShopContext from '../../shop-context'
import { useContext } from 'react'


function Card(props) {
  const [like, setLike] = useState(false)
  const shopContext = useContext(ShopContext)
  const liked = ()=>{
    setLike(true)
    shopContext.addItem({ productName : props.pro,
    productImg : "https://static.reserved.com/media/catalog/product/cache/1200/a4e40ebdc3e371adff845072e1c73f37/1/3/1300O-90M-002-1-651347.jpg",
    productPrice : 249.99,
    id: Math.random()*100+1})
  }
  
  const unliked = ()=>{
    setLike(false)
  }
  return (
    <div className={classes.card}>
      <img src={props.productImg} alt="" />
      <div className={classes.sideModule}>
        <h3>{props.productName}</h3>
        {!like?<Heart onClick={liked}/>: <HeartFull onClick={unliked} color={"#c70000"}/> }
      </div>
      <p>{props.productPrice}z≈Ç</p>
    </div>
    )
}

export default Card
