import ShopContext from "./shop-context"
import { useReducer } from "react"

const defaultCartState = {
    items: [],
    totalAmount: 0,
    size: []
}

const shopReducer = (state, action)=>{
    if(action.type ==="ADD"){ 
        const newItem  = action.item  
       return {...state, items: [...state.items, newItem]} 
    }else if(action.type = "REMOVE"){
        const deletedItem = action.id 
        const newItems = state.items.filter((item)=>{
            return item.id !== deletedItem
        })
        return {...state, items: [...newItems]}   
    }else if(action.type = "SET_SIZE"){
        console.log(action.size)
        return {...state, size:[...action.size]} 
    }
}

const countTotal=(items)=>{
    if(items.length){
        let total = 0
        items.map(item=>{
            total += Number(item.productPrice)
        })
        return total
    }else{
        return 0 
    }
    
}

const ShopProvider = props=>{
    const [shopState, dispatchShopAction]=useReducer(shopReducer, defaultCartState)
    const addItemToCart = item=>{
        dispatchShopAction({type: "ADD", item})
    }
    const removeItemFromCart = id=>{
        dispatchShopAction({type: "REMOVE", id})
    }
    const setSize = (size)=>{
        dispatchShopAction({type: "SET_SIZE", size})
    }
    const shopContext ={
        items:shopState.items,
        totalAmount: countTotal(shopState.items),
        itemsAmount: shopState.items.length,
        addItem: addItemToCart,
        removeItem: removeItemFromCart,
        setSize: setSize,
        size: shopState.size
    }
    return <ShopContext.Provider value={shopContext}>
        {props.children}
    </ShopContext.Provider>
}

export default ShopProvider
